import React, {Component} from "react";
import './todoItem.css';
class TodoItem extends Component{
    constructor(props) {
        super(props);
    }
    removeTodo(id){
     this.props.removeTodo(id);
    }

    render() {
        return (
            <div className="todoWrapper">
               <button className="removeTodo" onclick={(e) => this.removeTodo(this.props.id)}>Remove</button>{this.props.todo}
            </div>
    )

    }
}
export default TodoItem;
